import { useState, useEffect } from 'react';
import axios from 'axios';

const apiBase = "http://localhost:5000";
function App() {

  const [todos, setTodos] = useState([]);
  const [popupActive, setPopupActive] = useState(false);
  const [newTodo, setNewTodo] = useState("");

  useEffect(async() => {
    await GetTodos();
    console.log(todos);
  }, [])

  const GetTodos = async () => {
    await axios.get(apiBase + "/todos")
      .then(response => setTodos(response.data))
      .catch(err => console.error("Error: ", err));
  }

  const completeTodo = async id => {
		const data = await fetch(apiBase + '/todo/complete/' + id).then(res => res.json());

		setTodos(todos => todos.map(todo => {
			if (todo._id === data._id) {
				todo.complete = data.complete;
			}

			return todo;
		}));

	};

  const addTodo = async () => {
		const data = await fetch(apiBase + "/todo/new", {
			method: "POST",
			headers: {
				"Content-Type": "application/json" 
			},
			body: JSON.stringify({
				text: newTodo
			})
		}).then(res => res.json());

		setTodos([...todos, data]);

		setPopupActive(false);
		setNewTodo("");
	}

  const deleteTodo = async id => {
		const data = await fetch(apiBase + '/todo/delete/' + id, { method: "DELETE" }).then(res => res.json());

		setTodos(todos => todos.filter(todo => todo._id !== data.result._id));
	}

  return (
    <div className="App">
      <h1>Hello, Prashant</h1>
      <h4>Your Tasks</h4>
      <div className="todos">
        {
          todos.map(todo => (
            <div key={todo._id} className={"todo " + (todo.complete ? "is-complete" : "")}
              onClick={() => completeTodo(todo._id)}>
              <div className="checkbox"></div>
              <div className="text">{todo.text}</div>
              {/* <button className='delete-todo' onClick={() => handleDelete(todo._id)}>Delete</button> */}
              <div className="delete-todo" onClick={() => deleteTodo(todo._id)}>x</div>
            </div>
          ))}
      </div>
      <div className='addPopup' onClick={() => setPopupActive(true)}>+</div>
      {
        popupActive ? (
          <div className='popup'>
            <div className='closePopup' onClick={() => setPopupActive(false)}>x</div>
            <div className='content'>
              <h3>Add task</h3>
              <input 
                type='text'
                className="add-todo-input"
                onChange={e => setNewTodo(e.target.value)}
                value={newTodo}
              />
              <button className='button' onClick={addTodo}>Add task</button>
            </div>
          </div>
        ) : '' }
    </div>
  );
}

export default App;
