import { useState, useEffect } from 'react';
import axios from 'axios';

const apiBase = "http://localhost:5000";
function App() {

  const [todos, setTodos] = useState([]);
  const [popupActive, setPopupActive] = useState(false);
  const [newTodo, setNewTodo] = useState("");

  useEffect(() => {
    GetTodos();
    console.log(todos);
  }, [])

  const GetTodos = async () => {
    await axios.get(apiBase + "/todos")
      .then(response => setTodos(response.data))
      .catch(err => console.error("Error: ", err));
  }

  const completeTodo = async id => {
		const data = await fetch(apiBase + '/todo/complete/' + id).then(res => res.json());

		setTodos(todos => todos.map(todo => {
			if (todo._id === data._id) {
				todo.complete = data.complete;
			}

			return todo;
		}));

	};

  // const deleteTodo = async id => {
	// 	const data = await fetch(apiBase + '/todo/delete/' + id, { method: "DELETE" }).then(res => res.json());

	// 	setTodos(todos => todos.filter(todo => todo._id !== data.result._id));
	// }

  return (
    <div className="App">
      <h1>Hello, Prashant</h1>
      <h4>Your Tasks</h4>
      <div className="todos">
        {
          todos.map(todo => (
            <div key={todo._id} className={"todo " + (todo.complete ? "is-complete" : "")}
              onClick={() => completeTodo(todo._id)}>
              <div className="checkbox"></div>
              <div className="text">{todo.text}</div>
              {/* <div className="delete-todo" onClick={() => deleteTodo(todo._id)}>x</div> */}
            </div>
          ))}
      </div>
    </div>
  );
}

export default App;
